describe('validators.email', function() {
  var email = validate.validators.email;
  email = email.bind(email);

  afterEach(function() {
    delete validate.validators.email.message;
    delete validate.validators.email.options;
  });

  it("allows empty values", function() {
    expect(email(null, {})).not.toBeDefined();
    expect(email(undefined, {})).not.toBeDefined();
  });

  it("doesn't allow non strings", function() {
    expect(email(3.14, {})).toBeDefined();
    expect(email(true, {})).toBeDefined();
  });

  it("allows valid emails", function() {
    expect(email('nicklas@ansman.se', {})).not.toBeDefined();
    expect(email('NiCkLaS@AnSmAn.Se', {})).not.toBeDefined();
    // Source: https://en.wikipedia.org/wiki/Email_address#Valid_email_addresses
    expect(email('niceandsimple@example.com', {})).not.toBeDefined();
    expect(email('very.common@example.com', {})).not.toBeDefined();
    expect(email('a.little.lengthy.but.fine@dept.example.com', {})).not.toBeDefined();
    expect(email('disposable.style.email.with+symbol@example.com', {})).not.toBeDefined();
    expect(email('other.email-with-dash@example.com', {})).not.toBeDefined();
    expect(email('@example.com', {})).not.toBeDefined();
    expect(email("foo@some.customtld", {})).not.toBeDefined();
  });

  it("doesn't allow 'invalid' emails", function() {
    var expected = "is not a valid email";
    expect(email("", {})).toEqual(expected);
    expect(email(" ", {})).toEqual(expected);
    expect(email("foobar", {})).toEqual(expected);
    expect(email("foo@bar", {})).toEqual(expected);

    // Source: https://en.wikipedia.org/wiki/Email_address#Invalid_email_addresses
    expect(email('abc.example.com', {})).toEqual(expected);
    expect(email('a@b@c@example.com', {})).toEqual(expected);
    expect(email('a"b(c)d,e:f;g<h>i[j\\k]l@example.com', {})).toEqual(expected);
    expect(email('just"not"right@example.com', {})).toEqual(expected);
    expect(email('this is"not\\allowed@example.com', {})).toEqual(expected);
    expect(email('this\\ still\\"not\\\\allowed@example.com', {})).toEqual(expected);
  });

  it("allows you to customize the error message", function() {
    validate.validators.email.message = "is totally not an email";
    expect(email("foobar", {})).toEqual("is totally not an email");
    var options = {message: "some other message"};
    expect(email("foobar", options)).toEqual("some other message");
  });

  it("supports default options", function() {
    validate.validators.email.options = {message: "barfoo"};
    var options = {message: 'foobar'};
    expect(email("foo", options)).toEqual('foobar');
    expect(email("foo", {})).toEqual('barfoo');
    expect(validate.validators.email.options).toEqual({message: "barfoo"});
    expect(options).toEqual({message: "foobar"});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message}
      , value = "foo";
    expect(email(value, options)).toBe(message);
  });
});

describe('validators.equality', function() {
  var equality = validate.validators.equality;
  equality = equality.bind(equality);

  afterEach(function() {
    delete validate.validators.equality.message;
    delete validate.validators.equality.options;
  });

  it("allows empty values", function() {
    expect(equality(null, "bar", "foo", {})).not.toBeDefined();
    expect(equality(undefined, "bar", "foo", {})).not.toBeDefined();
  });

  it("supports equality with another attribute", function() {
    expect(equality("", "bar", "foo", {foo: "foo"})).toBeDefined();
    expect(equality("  ", "bar", "foo", {foo: "foo"})).toBeDefined();
    expect(equality("foo", "bar", "foo", {foo: "foo"})).toBeDefined();
    expect(equality("foo", "bar", "foo", {foo: "foo", bar: "bar"})).toBeDefined();
    expect(equality("foo", "bar", "foo", {foo: "foo", bar: "foo"})).not.toBeDefined();

    expect(equality(1, "bar", "foo", {foo: 1})).toBeDefined();
    expect(equality(1, "bar", "foo", {foo: 1, bar: 2})).toBeDefined();
    expect(equality(1, "bar", "foo", {foo: 1, bar: 1})).not.toBeDefined();
  });

  it("has a nice default message", function() {
    var constraints = {
      foo: {
        equality: "fooBar"
      }
    };
    expect(equality("foo", "fooBar", "foo", {foo: "foo"})).toEqual("is not equal to foo bar");
  });

  it("allows you to customize the error message", function() {
    validate.validators.equality.message = "is totally not equal to %{attribute}";
    expect(equality("foo", "bar", "foo", {foo: "foo"})).toEqual("is totally not equal to bar");
    var options = {attribute: "bar", message: "some other message %{attribute}"};
    expect(equality("foo", options, "foo", {foo: "foo"})).toEqual("some other message bar");
  });

  it("supports default options", function() {
    validate.validators.equality.options = {message: "barfoo", attribute: "bar"};
    var options = {message: 'foobar', attribute: "baz"};
    expect(equality("foo", options, "foo", {foo: "foo"})).toEqual('foobar');
    expect(equality("foo", {}, "foo", {foo: "foo"})).toEqual('barfoo');
    expect(equality("foo", {message: "foobar"}, "foo", {foo: "foo"})).toEqual('foobar');
    expect(validate.validators.equality.options).toEqual({message: "barfoo", attribute: "bar"});
    expect(options).toEqual({message: "foobar", attribute: "baz"});
  });

  it("supports nested objects", function() {
    expect(equality("foo", "bar.baz", "foo", {foo: "foo"})).toBeDefined();
    expect(equality("foo", "bar.baz", "foo", {foo: "foo", bar: {baz: "baz"}})).toBeDefined();
    expect(equality("foo", "bar.baz", "foo", {foo: "foo", bar: {baz: "foo"}})).not.toBeDefined();
  });

  it("throws an error if the attribute option isn't provided", function() {
    function run(options) {
      equality("foo", options, "foo", {foo: "foo"});
    }

    expect(run.bind({})).toThrow();
    expect(run.bind({attribute: null})).toThrow();
    expect(run.bind({attribute: 4711})).toThrow();
    expect(run.bind({attribute: false})).toThrow();
    expect(run.bind({attribute: undefined})).toThrow();
    expect(run.bind({attribute: ""})).toThrow();
  });

  it("accepts an optional comparator", function() {
    var complex = {foo: "bar"}
      , other = {bar: "foo"}
      , comparator = jasmine.createSpy("comparator").and.returnValue(true)
      , attributes = {complex: complex, other: other}
      , constraints = {
          complex: {
            equality: {
              attribute: "other",
              comparator: comparator
            }
          }
        };

    expect(validate(attributes, constraints)).not.toBeDefined();
    expect(comparator)
      .toHaveBeenCalledWith(complex,
                            other,
                            constraints.complex.equality,
                            "complex",
                            attributes);

    comparator.and.returnValue(false);
    expect(validate(attributes, constraints)).toBeDefined();
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message, attribute: "bar"}
      , value = "foo";
    expect(equality(value, options)).toBe(message);
  });
});

describe("validators.exclusion", function() {
  var exclusion = validate.validators.exclusion.bind(validate.validators.exclusion)
    , within = ["foo", "bar", "baz"];

  afterEach(function() {
    delete validate.validators.exclusion.message;
    delete validate.validators.exclusion.options;
  });

  it("allows empty values", function() {
    expect(exclusion(null, {})).not.toBeDefined();
    expect(exclusion(undefined, {})).not.toBeDefined();
  });

  it("returns nothing if the value is allowed", function() {
    var opts = {within: within};
    expect(exclusion("", {})).not.toBeDefined();
    expect(exclusion(" ", {})).not.toBeDefined();
    expect(exclusion("quux", opts)).not.toBeDefined();
    expect(exclusion(false, opts)).not.toBeDefined();
    expect(exclusion(1, opts)).not.toBeDefined();
  });

  it("returns an error if the value is not allowed", function() {
    var opts = {within: within};
    expect(exclusion("foo", opts)).toEqual("^foo is restricted");
    expect(exclusion("bar", opts)).toEqual("^bar is restricted");
    expect(exclusion("baz", opts)).toEqual("^baz is restricted");
  });

  it("allows you to customize the message", function() {
    validate.validators.exclusion.message = "^%{value} isn't great";
    var opts = {within: within};
    expect(exclusion("foo", opts)).toEqual("^foo isn't great");

    opts.message = "^The value %{value} is not valid";
    expect(exclusion("foo", opts)).toEqual("^The value foo is not valid");
  });

  it("uses the keys if the within value is an object", function() {
    expect(exclusion("foo", {within: {foo: true}})).toBeDefined();
    expect(exclusion("bar", {within: {foo: true}})).not.toBeDefined();
  });

  it("uses the options as the within list if the options is an array", function() {
    expect(exclusion("foo", ["foo", "bar"])).toBeDefined();
    expect(exclusion("baz", ["foo", "bar"])).not.toBeDefined();
  });

  it("supports default options", function() {
    validate.validators.exclusion.options = {
      message: "barfoo",
      within: [1, 2, 3]
    };
    var options = {message: 'foobar'};
    expect(exclusion(2, options)).toEqual('foobar');
    expect(exclusion(2, {})).toEqual('barfoo');
    expect(validate.validators.exclusion.options).toEqual({
      message: "barfoo",
      within: [1, 2, 3]
    });
    expect(options).toEqual({message: "foobar"});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message, within: ["foo"]}
      , value = "foo";
    expect(exclusion(value, options)).toBe(message);
  });
});

describe("validators.format", function() {
  var format = validate.validators.format.bind(validate.validators.format)
    , options1 = {pattern: /^foobar$/i}
    , options2 = {pattern: "^foobar$", flags: "i"};

  afterEach(function() {
    delete validate.validators.format.message;
    delete validate.validators.format.options;
  });

  it("allows empty values", function() {
    expect(format(null, options1)).not.toBeDefined();
    expect(format(null, options2)).not.toBeDefined();
    expect(format(undefined, options1)).not.toBeDefined();
    expect(format(undefined, options2)).not.toBeDefined();
  });

  it("allows values that matches the pattern", function() {
    expect(format("fooBAR", options1)).not.toBeDefined();
    expect(format("fooBAR", options2)).not.toBeDefined();
  });

  it("doesn't allow values that doesn't matches the pattern", function() {
    expect(format("", options1)).toBeDefined("is invalid");
    expect(format("", options2)).toBeDefined("is invalid");
    expect(format(" ", options1)).toBeDefined("is invalid");
    expect(format(" ", options2)).toBeDefined("is invalid");
    expect(format("barfoo", options1)).toEqual("is invalid");
    expect(format("barfoo", options2)).toEqual("is invalid");
  });

  it("non strings are not allowed", function() {
    var obj = {toString: function() { return "foobar"; }};
    expect(format(obj, options1)).toBeDefined();
    expect(format(obj, options2)).toBeDefined();
    expect(format(3, options1)).toBeDefined();
    expect(format(3, options2)).toBeDefined();
  });

  it("non strings are not allowed", function() {
    expect(format(3, options1)).toBeDefined();
    expect(format(3, options2)).toBeDefined();
  });

  it("doesn't allow partial matches", function() {
    var options1 = {pattern: /\.png$/g}
      , options2 = {pattern: "\\.png$", flags: "g"};
    expect(format("foo.png", options1)).toBeDefined();
    expect(format("foo.png", options2)).toBeDefined();
  });

  it("allows a custom message", function() {
    validate.validators.format.message = "is using a default message";

    var options = {pattern: /^[a-z]+$/g};
    expect(format("4711", options)).toEqual("is using a default message");

    options.message = "must only contain a-z";
    expect(format("4711", options)).toEqual("must only contain a-z");
  });

  it("supports the options being the pattern", function() {
    expect(format("barfoo", options1.pattern)).toBeDefined();
    expect(format("barfoo", options2.pattern)).toBeDefined();
  });

  it("supports default options", function() {
    validate.validators.format.options = {
      message: "barfoo",
      pattern: "abc"
    };
    var options = {message: 'foobar'};
    expect(format("cba", options)).toEqual('foobar');
    expect(format("cba", {})).toEqual('barfoo');
    expect(validate.validators.format.options).toEqual({
      message: "barfoo",
      pattern: "abc"
    });
    expect(options).toEqual({message: "foobar"});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message, pattern: /bar/}
      , value = "foo";
    expect(format(value, options)).toBe(message);
  });
});

