describe('validator.length', function() {
  var length = validate.validators.length.bind(validate.validators.length);

  afterEach(function() {
    delete validate.validators.length.notValid;
    delete validate.validators.length.tooLong;
    delete validate.validators.length.tooShort;
    delete validate.validators.length.wrongLength;
    delete validate.validators.length.options;
  });

  describe("is", function() {
    it("allows you to specify a fixed length the object has to be", function() {
      var value = {length: 10}
        , options = {is: 10};
      expect(length(value, options)).not.toBeDefined();

      options.is = 11;
      var expected = ["is the wrong length (should be 11 characters)"];
      expect(length(value, options)).toEqual(expected);
    });

    it("allows a custom message", function() {
      var value = {length: 10}
        , options = {is: 11};

      validate.validators.length.wrongLength = "default %{count}";
      expect(length(value, options)).toEqual(["default 11"]);

      options.wrongLength = "wrongLength %{count}";
      expect(length(value, options)).toEqual(["wrongLength 11"]);
    });
  });

  describe("minimum", function() {
    it("allows you to specify a minimum value", function() {
      var value = {length: 10}
        , options = {minimum: 10};
      expect(length(value, options)).not.toBeDefined();

      options.minimum = 11;
      var expected = ["is too short (minimum is 11 characters)"];
      expect(length(value, options)).toEqual(expected);
    });

    it("allows a custom message", function() {
      var value = {length: 10}
        , options = {minimum: 11};

      validate.validators.length.tooShort = "default %{count}";

      expect(length(value, options)).toEqual(["default 11"]);

      options.tooShort = "tooShort %{count}";
      expect(length(value, options)).toEqual(["tooShort 11"]);
    });
  });

  describe("maximum", function() {
    it("allows you to specify a maximum value", function() {
      var value = {length: 11}
        , options = {maximum: 11};
      expect(length(value, options)).not.toBeDefined();

      options.maximum = 10;
      var expected = ["is too long (maximum is 10 characters)"];
      expect(length(value, options)).toEqual(expected);
    });

    it("allows a custom message", function() {
      var value = {length: 11}
        , options = {maximum: 10};

      validate.validators.length.tooLong = "default %{count}";
      expect(length(value, options)).toEqual(["default 10"]);

      options.tooLong = "tooLong %{count}";
      expect(length(value, options)).toEqual(["tooLong 10"]);
    });
  });

  it("allows empty values", function() {
    var options = {is: 10, minimum: 20, maximum: 5};
    expect(length(null, options)).not.toBeDefined();
    expect(length(undefined, options)).not.toBeDefined();
  });

  it("refuses values without a numeric length property", function() {
    spyOn(validate, "error");
    var options = {is: 10, minimum: 10, maximum: 20};
    expect(length(3.1415, options)).toBeDefined();
    expect(length(-3.1415, options)).toBeDefined();
    expect(length(0, options)).toBeDefined();
    expect(length({foo: "bar"}, options)).toBeDefined();
    expect(length({lengthi: 10}, options)).toBeDefined();
    expect(length({length: "foo"}, options)).toBeDefined();
    expect(length(3, {})).toBeDefined();
    expect(validate.error).toHaveBeenCalled();
  });

  // This test is not a real life example, specifying is with anything else
  // is just weird but hey.
  it("allows you to specify is, minimum and maximum", function() {
    var options = {
          is: 10,
          minimum: 20,
          maximum: 5
      };
      expect(length({length: 9}, options)).toHaveLength(3);
      expect(length("foobar", options)).toHaveLength(3);
      expect(length("", options)).toHaveLength(2);
      expect(length(" ", options)).toHaveLength(2);
  });

  it("return the message only once if specified", function() {
    var value = {length: 9}
      , options = {
          message: "my message",
          is: 10,
          minimum: 20,
          maximum: 5
        };
      expect(length(value, options)).toBe("my message");
  });

  it("doesn't override specific messages with the default one", function() {
    var value = {length: 3}
      , options = {
        is: 2,
        minimum: 4,
        maximum: 2,
        wrongLength: "wrongLength",
        tooLong: "tooLong",
        tooShort: "tooShort"
      };

      validate.validators.length.notValid = "default message";
      expect(length(value, options)).toHaveItems([
        "wrongLength",
        "tooLong",
        "tooShort"
      ]);
  });

  describe("tokenizer", function() {
    it("allows you to count words for example", function() {
      var options = {
        maximum: 2,
        tokenizer: function(value) { return value.split(/\s+/g); }
      };

      expect(length("foo bar", options)).not.toBeDefined();
      expect(length("foo bar baz", options)).toBeDefined();
    });
  });

  it("supports default options", function() {
    var tokenizer = jasmine.createSpy("tokenizer").and.returnValue({length: 3});
    validate.validators.length.options = {
      minimum: 10,
      tokenizer: tokenizer,
      message: "barfoo"
    };
    var options = {message: 'foobar'};
    expect(length(4, options)).toEqual('foobar');
    expect(length(4, {})).toEqual('barfoo');
    expect(tokenizer).toHaveBeenCalled();
    expect(validate.validators.length.options).toEqual({
      minimum: 10,
      tokenizer: tokenizer,
      message: "barfoo"
    });
    expect(options).toEqual({message: "foobar"});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message, minimum: 10}
      , value = "foo";
    expect(length(value, options)).toBe(message);
  });
});

describe("validators.numericality", function() {
  var numericality = validate.validators.numericality
      .bind(validate.validators.numericality);

  afterEach(function() {
    var n = validate.validators.numericality;
    delete n.message;
    delete n.notValid;
    delete n.notInteger;
    delete n.notOdd;
    delete n.notEven;
    delete n.notGreaterThan;
    delete n.notGreaterThanOrEqualTo;
    delete n.notEqualTo;
    delete n.notLessThan;
    delete n.notLessThanOrEqualTo;
    delete n.notDivisibleBy;
    delete n.options;
  });

  it("allows empty values", function() {
    expect(numericality(null, {})).not.toBeDefined();
    expect(numericality(undefined, {})).not.toBeDefined();
  });

  it("allows numbers", function() {
    expect(numericality(3.14, {})).not.toBeDefined();
    expect(numericality("3.14", {})).not.toBeDefined();
  });

  it("doesn't allow non numbers", function() {
    var e = "is not a number";
    expect(numericality("", {})).toEqual(e);
    expect(numericality("  ", {})).toEqual(e);
    expect(numericality("foo", {})).toEqual(e);
    expect(numericality(NaN, {})).toEqual(e);
    expect(numericality(false, {})).toEqual(e);
    expect(numericality([1], {})).toEqual(e);
    expect(numericality({foo: "bar"}, {})).toEqual(e);
  });

  it("doesn't allow strings if noStrings is true", function() {
    expect(numericality("3.14", {noStrings: true})).toBeDefined();
  });

  it("uses the message if specified", function() {
    validate.validators.numericality.notValid = "default message";
    expect(numericality("foo", {})).toEqual("default message");
    expect(numericality("foo", {message: "my message"})).toEqual("my message");
  });

  it("uses the custom message if specified", function() {
    validate.validators.numericality.message = "default generic message";
    expect(numericality("foo", {})).toEqual("default generic message");
    var expected = "default message";
    expect(numericality("foo", {notValid: expected})).toEqual("default message");
    expect(numericality("foo", {message: "my message"})).toEqual("my message");
  });

  describe("onlyInteger", function() {
    it("allows integers", function() {
      expect(numericality(1, {onlyInteger: true})).not.toBeDefined();
    });

    it("doesn't allow real numbers", function() {
      var expected = "must be an integer";
      expect(numericality(3.14, {onlyInteger: true})).toEqual(expected);
    });

    it("uses the message if specified", function() {
      var opts = {onlyInteger: true};

      validate.validators.numericality.message = "default generic message";
      expect(numericality(3.14, opts)).toEqual("default generic message");

      validate.validators.numericality.notInteger = "default message";
      expect(numericality(3.14, opts)).toEqual("default message");

      opts.message = "my message";
      expect(numericality(3.14, opts)).toEqual("my message");
    });

    it("uses the custom message if specified", function() {
      var opts = {onlyInteger: true, notInteger: "default message" };
      expect(numericality(3.14, opts)).toEqual("default message");
    });
  });

  describe("greaterThan", function() {
    it("allows numbers that are greater than", function() {
      expect(numericality(3.14, {greaterThan: 2.72})).not.toBeDefined();
    });

    it("doesn't allow numbers that are smaller than or equal to", function() {
      var expected = ["must be greater than 3.14"];
      expect(numericality(3.14, {greaterThan: 3.14})).toEqual(expected);
      expect(numericality(2.72, {greaterThan: 3.14})).toEqual(expected);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(3.14, {greaterThan: 3.14, notGreaterThan: expected})).toEqual([expected]);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(3.14, {greaterThan: 3.14})).toEqual(["default generic message"]);

      validate.validators.numericality.notGreaterThan = "default message";
      expect(numericality(3.14, {greaterThan: 3.14})).toEqual(["default message"]);
    });
  });

  describe("greaterThanOrEqualTo", function() {
    it("allows numbers that are greater than or equal to", function() {
      expect(numericality(3.14, {greaterThanOrEqualTo: 2.72})).not.toBeDefined();
      expect(numericality(2.72, {greaterThanOrEqualTo: 2.72})).not.toBeDefined();
    });

    it("doesn't allow numbers that are smaller than", function() {
      var expected = ["must be greater than or equal to 3.14"];
      expect(numericality(2.72, {greaterThanOrEqualTo: 3.14})).toEqual(expected);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(3.13, {greaterThanOrEqualTo: 3.14, notGreaterThanOrEqualTo: expected})).toEqual([expected]);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(3.13, {greaterThanOrEqualTo: 3.14})).toEqual(["default generic message"]);

      validate.validators.numericality.notGreaterThanOrEqualTo = "default message";
      expect(numericality(3.13, {greaterThanOrEqualTo: 3.14})).toEqual(["default message"]);
    });
  });

  describe("equalTo", function() {
    it("allows numbers that are equal to", function() {
      expect(numericality(2.72, {equalTo: 2.72})).not.toBeDefined();
    });

    it("doesn't allow numbers that are not equal", function() {
      var expected = ["must be equal to 2.72"];
      expect(numericality(3.14, {equalTo: 2.72})).toEqual(expected);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(3.13, {equalTo: 3.14})).toEqual(["default generic message"]);

      validate.validators.numericality.notEqualTo = "default message";
      expect(numericality(3.13, {equalTo: 3.14})).toEqual(["default message"]);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(3.13, {equalTo: 3.14, notEqualTo:expected})).toEqual([expected]);
    });
  });

  describe("lessThan", function() {
    it("allows numbers that are less than", function() {
      expect(numericality(2.72, {lessThan: 3.14})).not.toBeDefined();
    });

    it("doesn't allow numbers that are greater than or equal to", function() {
      var expected = ["must be less than 2.72"];
      expect(numericality(2.72, {lessThan: 2.72})).toEqual(expected);
      expect(numericality(3.14, {lessThan: 2.72})).toEqual(expected);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(3.14, {lessThan: 3.14})).toEqual(["default generic message"]);

      validate.validators.numericality.notLessThan = "default message";
      expect(numericality(3.14, {lessThan: 3.14})).toEqual(["default message"]);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(3.14, {lessThan: 3.14, notLessThan: expected})).toEqual([expected]);
    });
  });

  describe("lessThanOrEqualTo", function() {
    it("allows numbers that are less than or equal to", function() {
      expect(numericality(2.72, {lessThanOrEqualTo: 3.14})).not.toBeDefined();
      expect(numericality(3.14, {lessThanOrEqualTo: 3.14})).not.toBeDefined();
    });

    it("doesn't allow numbers that are greater than", function() {
      var expected = ["must be less than or equal to 2.72"];
      expect(numericality(3.14, {lessThanOrEqualTo: 2.72})).toEqual(expected);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(3.15, {lessThanOrEqualTo: 3.14})).toEqual(["default generic message"]);

      validate.validators.numericality.notLessThanOrEqualTo = "default message";
      expect(numericality(3.15, {lessThanOrEqualTo: 3.14})).toEqual(["default message"]);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(3.15, {lessThanOrEqualTo: 3.14, notLessThanOrEqualTo: expected})).toEqual([expected]);
    });
  });

  describe("divisibleBy", function() {
    it("allows numbers divisible by the value", function() {
      expect(numericality(0, {divisibleBy: 2})).not.toBeDefined();
      expect(numericality(5, {divisibleBy: 5})).not.toBeDefined();
      expect(numericality(16, {divisibleBy: 8})).not.toBeDefined();
    });

    it("doesn't allow numbers that are not divisible by the given number", function() {
      var expected = ["must be divisible by 100"];
      expect(numericality(121, {divisibleBy: 100})).toEqual(expected);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(161, {divisibleBy: 200})).toEqual(["default generic message"]);

      validate.validators.numericality.notDivisibleBy = "default message";
      expect(numericality(161, {divisibleBy: 200})).toEqual(["default message"]);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(133, {divisibleBy: 4, notDivisibleBy: expected})).toEqual([expected]);
    });
  });

  describe("odd", function() {
    it("allows odd numbers", function() {
      expect(numericality(1, {odd: true})).not.toBeDefined();
      expect(numericality(3, {odd: true})).not.toBeDefined();
      expect(numericality(5, {odd: true})).not.toBeDefined();
    });

    it("disallows even numbers", function() {
      var expected = ["must be odd"];
      expect(numericality(0, {odd: true})).toEqual(expected);
      expect(numericality(2, {odd: true})).toEqual(expected);
      expect(numericality(4, {odd: true})).toEqual(expected);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(2, {odd: true})).toEqual(["default generic message"]);

      validate.validators.numericality.notOdd = "default message";
      expect(numericality(2, {odd: true})).toEqual(["default message"]);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(2, {odd: true, notOdd: expected})).toEqual([expected]);
    });
  });

  describe("even", function() {
    it("allows even numbers", function() {
      expect(numericality(0, {even: true})).not.toBeDefined();
      expect(numericality(2, {even: true})).not.toBeDefined();
      expect(numericality(4, {even: true})).not.toBeDefined();
    });

    it("disallows odd numbers", function() {
      var expected = ["must be even"];
      expect(numericality(1, {even: true})).toEqual(expected);
      expect(numericality(3, {even: true})).toEqual(expected);
      expect(numericality(5, {even: true})).toEqual(expected);
    });

    it("allows for a default message", function() {
      validate.validators.numericality.message = "default generic message";
      expect(numericality(3, {even: true})).toEqual(["default generic message"]);

      validate.validators.numericality.notEven = "default message";
      expect(numericality(3, {even: true})).toEqual(["default message"]);
    });

    it("allows for a custom message", function() {
      var expected = "custom message";
      expect(numericality(3, {even: true, notEven: expected})).toEqual([expected]);
    });
  });

  it("can return multiple errors", function() {
    var options = {
      greaterThan: 10,
      greaterThanOrEqualTo: 10,
      lessThan: 5,
      lessThanOrEqualTo: 5,
      divisibleBy: 10,
      equalTo: 20,
      odd: true,
      even: true
    };
    expect(numericality(7.2, options)).toHaveLength(8);
  });

  it("returns options.message only once", function() {
    var options = {
      greaterThan: 10,
      greaterThanOrEqualTo: 10,
      lessThan: 5,
      lessThanOrEqualTo: 5,
      divisibleBy: 10,
      equalTo: 20,
      odd: true,
      even: true,
      message: 'my message'
    };
    expect(numericality(7.2, options)).toEqual("my message");
  });

  it("supports default options", function() {
    validate.validators.numericality.options = {
      greaterThan: 10,
      message: "barfoo"
    };
    var options = {message: 'foobar'};
    expect(numericality(4, options)).toEqual('foobar');
    expect(numericality(4, {})).toEqual('barfoo');
    expect(validate.validators.numericality.options).toEqual({
      greaterThan: 10,
      message: "barfoo"
    });
    expect(options).toEqual({message: "foobar"});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message}
      , value = "foo";
    expect(numericality(value, options)).toBe(message);
  });

  describe("strict", function() {
    it("disallows prefixed zeros", function() {
      expect(numericality("01.0", {strict: true}))
        .toEqual("must be a valid number");
      expect(numericality("0001.0000000", {strict: true}))
        .toEqual("must be a valid number");
      expect(numericality("020", {strict: true}))
        .toEqual("must be a valid number");
      expect(numericality("1.00", {strict: true, onlyInteger: true}))
        .toEqual("must be a valid number");
      expect(numericality("1.", {strict: true}))
        .toEqual("must be a valid number");
      expect(numericality("1.", {strict: true, onlyInteger: true}))
        .toEqual("must be a valid number");
      expect(numericality(".0", {strict: true}))
        .toEqual("must be a valid number");
      expect(numericality(".1", {strict: true}))
        .toEqual("must be a valid number");

      expect(numericality("1.00", {strict: true})).not.toBeDefined();
      expect(numericality("1.0", {strict: true})).not.toBeDefined();
      expect(numericality(10, {strict: true})).not.toBeDefined();
      expect(numericality("10", {strict: true})).not.toBeDefined();
      expect(numericality("0.1", {strict: true})).not.toBeDefined();
      expect(numericality("0", {strict: true})).not.toBeDefined();
    });
  });

  it("allows overriding the generic message", function() {
    validate.validators.numericality.message = "default generic message";
    expect(numericality("foo", {})).toEqual("default generic message");

    validate.validators.numericality.notValid = "default not valid message";
    expect(numericality("foo", {})).toEqual("default not valid message");

    expect(numericality("foo", {notValid: "not valid"})).toEqual("not valid");

    expect(numericality("foo", {message: "some error"})).toEqual("some error");
  });
});

describe('validator.presence', function() {
  var presence = validate.validators.presence.bind(validate.validators.presence);

  afterEach(function() {
    delete validate.validators.presence.message;
    delete validate.validators.presence.options;
  });

  it("doesn't allow empty values", function() {
    expect(presence('', {})).toBeDefined();
    expect(presence('  ', {})).toBeDefined();
    expect(presence(null, {})).toBeDefined();
    expect(presence(undefined, {})).toBeDefined();
    expect(presence([], {})).toBeDefined();
    expect(presence({}, {})).toBeDefined();
  });

  it("allows non empty values", function() {
    expect(presence('foo', {})).not.toBeDefined();
    expect(presence(0, {})).not.toBeDefined();
    expect(presence(false, {})).not.toBeDefined();
    expect(presence([null], {})).not.toBeDefined();
    expect(presence({foo: null}, {})).not.toBeDefined();
    expect(presence(function(){return null;}, {})).not.toBeDefined();
  });

  it("has a nice default message", function() {
    var msg = presence(null, {});
    expect(msg).toEqual("can't be blank");
  });

  it("has an option for allowing empty values", function() {
    expect(presence('', {allowEmpty: true})).not.toBeDefined();
    expect(presence('  ', {allowEmpty: true})).not.toBeDefined();
    expect(presence([], {allowEmpty: true})).not.toBeDefined();
    expect(presence({}, {allowEmpty: true})).not.toBeDefined();
  });

  it("also allows to specify your own nice message", function() {
    validate.validators.presence.message = "default message";
    expect(presence(null, {})).toEqual("default message");
    expect(presence(null, {message: "my message"})).toEqual("my message");
  });

  it("supports default options", function() {
    validate.validators.presence.options = {message: "barfoo"};
    var options = {message: 'foobar'};
    expect(presence(null, options)).toEqual('foobar');
    expect(presence(null, {})).toEqual('barfoo');
    expect(validate.validators.presence.options).toEqual({message: "barfoo"});
    expect(options).toEqual({message: "foobar"});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message}
      , value = null;
    expect(presence(value, options)).toBe(message);
  });
});

describe("validators.url", function() {
  var url = validate.validators.url;
  url = url.bind(url);

  afterEach(function() {
    delete validate.validators.url.message;
    delete validate.validators.url.options;
  });

  it("allows empty values", function() {
    expect(url(null, {})).not.toBeDefined();
    expect(url(undefined, {})).not.toBeDefined();
  });

  it("doesn't allow non strings", function() {
    expect(url(3.14, {})).toBeDefined();
    expect(url(true, {})).toBeDefined();
    expect(url({key: "i'm a string"}, {})).toBeDefined();
  });

  it("doesn't allow 'invalid' urls", function() {
    var expected = "is not a valid url";

    expect(url("", {})).toBeDefined();
    expect(url(" ", {})).toBeDefined();
    expect(url("http://", {})).toBeDefined();
    expect(url("http://.", {})).toBeDefined();
    expect(url("http://..", {})).toBeDefined();
    expect(url("http://../", {})).toBeDefined();
    expect(url("http://?", {})).toBeDefined();
    expect(url("http://??", {})).toBeDefined();
    expect(url("http://??/", {})).toBeDefined();
    expect(url("http://#", {})).toBeDefined();
    expect(url("http://##", {})).toBeDefined();
    expect(url("http://##/", {})).toBeDefined();
    expect(url("http://foo.bar?q=Spaces should be encoded", {})).toBeDefined();
    expect(url("//", {})).toBeDefined();
    expect(url("//a", {})).toBeDefined();
    expect(url("///a", {})).toBeDefined();
    expect(url("///", {})).toBeDefined();
    expect(url("http:///a", {})).toBeDefined();
    expect(url("foo.com", {})).toBeDefined();
    expect(url("rdar://1234", {})).toBeDefined();
    expect(url("h://test", {})).toBeDefined();
    expect(url("http:// shouldfail.com", {})).toBeDefined();
    expect(url(":// should fail", {})).toBeDefined();
    expect(url("http://foo.bar/foo(bar)baz quux", {})).toBeDefined();
    expect(url("ftps://foo.bar/", {})).toBeDefined();
    expect(url("http://-error-.invalid/", {})).toBeDefined();
    expect(url("http://-a.b.co", {})).toBeDefined();
    expect(url("http://a.b-.co", {})).toBeDefined();
    expect(url("http://0.0.0.0", {})).toBeDefined();
    expect(url("http://10.1.1.0", {})).toBeDefined();
    expect(url("http://10.1.1.255", {})).toBeDefined();
    expect(url("http://224.1.1.1", {})).toBeDefined();
    expect(url("http://1.1.1.1.1", {})).toBeDefined();
    expect(url("http://123.123.123", {})).toBeDefined();
    expect(url("http://3628126748", {})).toBeDefined();
    expect(url("http://.www.foo.bar/", {})).toBeDefined();
    expect(url("http://www.foo.bar./", {})).toBeDefined();
    expect(url("http://.www.foo.bar./", {})).toBeDefined();
    expect(url("http://10.1.1.1", {})).toBeDefined();
    expect(url("http://localhost", {})).toBeDefined();
  });

  it("allows valid urls", function() {
    expect(url("http://foo.com", {})).not.toBeDefined();
    expect(url("http://foo.com/", {})).not.toBeDefined();
    expect(url("http://foo.com/blah_blah", {})).not.toBeDefined();
    expect(url("http://foo.com/blah_blah/", {})).not.toBeDefined();
    expect(url("http://foo.com/blah_blah_(wikipedia)", {})).not.toBeDefined();
    expect(url("http://foo.com/blah_blah_(wikipedia)_(again)", {})).not.toBeDefined();
    expect(url("http://foo.com?query=bar", {})).not.toBeDefined();
    expect(url("http://foo.com#fragment=bar", {})).not.toBeDefined();
    expect(url("http://www.example.com/wpstyle/?p=364", {})).not.toBeDefined();
    expect(url("https://www.example.com/foo/?bar=baz&inga=42&quux", {})).not.toBeDefined();
    expect(url("https://www.example.com/foo/#bar=baz&inga=42&quux", {})).not.toBeDefined();
    expect(url("http://df.ws/123", {})).not.toBeDefined();
    expect(url("http://userid:password@example.com:8080", {})).not.toBeDefined();
    expect(url("http://userid:password@example.com:8080/", {})).not.toBeDefined();
    expect(url("http://userid@example.com", {})).not.toBeDefined();
    expect(url("http://userid@example.com/", {})).not.toBeDefined();
    expect(url("http://userid@example.com:8080", {})).not.toBeDefined();
    expect(url("http://userid@example.com:8080/", {})).not.toBeDefined();
    expect(url("http://userid:password@example.com", {})).not.toBeDefined();
    expect(url("http://userid:password@example.com/", {})).not.toBeDefined();
    expect(url("http://142.42.1.1/", {})).not.toBeDefined();
    expect(url("http://142.42.1.1:8080/", {})).not.toBeDefined();
    expect(url("http://.ws/", {})).not.toBeDefined();
    expect(url("http://.ws", {})).not.toBeDefined();
    expect(url("http://.ws/", {})).not.toBeDefined();
    expect(url("http://foo.com/blah_(wikipedia)#cite-1", {})).not.toBeDefined();
    expect(url("http://foo.com/blah_(wikipedia)_blah#cite-1", {})).not.toBeDefined();
    expect(url("http://foo.com/unicode_()_in_parens", {})).not.toBeDefined();
    expect(url("http://foo.com/(something)?after=parens", {})).not.toBeDefined();
    expect(url("http://.damowmow.com/", {})).not.toBeDefined();
    expect(url("http://code.google.com/events/#&product=browser", {})).not.toBeDefined();
    expect(url("http://j.mp", {})).not.toBeDefined();
    expect(url("http://foo.bar/?q=Test%20URL-encoded%20stuff", {})).not.toBeDefined();
    expect(url("http://.", {})).not.toBeDefined();
    expect(url("http://.", {})).not.toBeDefined();
    expect(url("http://.", {})).not.toBeDefined();
    expect(url("http://-.~_!$&'()*+,;=:%40:80%2f::::::@example.com", {})).not.toBeDefined();
    expect(url("http://1337.net", {})).not.toBeDefined();
    expect(url("http://a.b-c.de", {})).not.toBeDefined();
    expect(url("http://223.255.255.254", {})).not.toBeDefined();
    expect(url("http://a.b--c.de/", {})).not.toBeDefined();
  });

  it("allows local url and private networks if option is set", function() {
    expect(url("http://10.1.1.1", {allowLocal: true})).not.toBeDefined();
    expect(url("http://172.16.1.123", {allowLocal: true})).not.toBeDefined();
    expect(url("http://192.168.1.123", {allowLocal: true})).not.toBeDefined();
    expect(url("http://localhost/foo", {allowLocal: true})).not.toBeDefined();
    expect(url("http://localhost:4711/foo", {allowLocal: true})).not.toBeDefined();
    // Issue #95
    expect(url("http://servername01:8153/go/cctray.xml", {allowLocal: true})).not.toBeDefined();
    expect(url("http://nicklas:password@localhost:4711/foo", {allowLocal: true})).not.toBeDefined();
  });

  it("allows custom schemes option is set", function() {
    var options = {schemes: ['ftp', 'jdbc']};
    expect(url("ftp://foo.bar.com", options)).not.toBeDefined();
    expect(url("jdbc://foo.bar.com", options)).not.toBeDefined();
    expect(url("http://foo.bar.com", options)).toBeDefined();
  });

  it("allows you to customize the error message", function() {
    validate.validators.url.message = "is totally not an url";
    expect(url("foobar", {})).toEqual("is totally not an url");

    var options = { message: "is not an url dude" };
    expect(url("foobar", options)).toEqual("is not an url dude");
  });

  it("supports default options", function() {
    validate.validators.url.options = {message: "barfoo", allowLocal: true, schemes: ['myscheme']};
    var options = {message: 'foobar', allowLocal: true, schemes: ['myscheme']};
    expect(url("foo", options)).toEqual('foobar');
    expect(url("foo", {})).toEqual('barfoo');
    expect(validate.validators.url.options).toEqual({message: "barfoo", allowLocal: true, schemes: ['myscheme']});
    expect(options).toEqual({message: "foobar", allowLocal: true, schemes: ['myscheme']});
  });

  it("allows functions as messages", function() {
    var message = function() { return "foo"; };
    var options = {message: message}
      , value = "foo";
    expect(url(value, options)).toBe(message);
  });
});
